import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { API_ENDPOINT } from "./context";

const SingleMovie = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState({});
  console.log(id, `${API_ENDPOINT}&i=${id}`);

  const fetchMovie = async () => {
    try {
      const response = await fetch(`${API_ENDPOINT}&i=${id}`);
      const data = await response.json();
      const { Response } = data;
      if (Response === "False") {
        console.log("no movie");
      } else {
        setMovie(data);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchMovie();
  }, []);

  console.log("movie", movie);
  if (!movie) return <h2>No movie</h2>;

  const { Title: title, Year: year, Plot: description, Poster: image } = movie;
  console.log(title, year, description, image);
  return (
    <div className="single-movie">
      movie - {id}
      <img src={image} alt={title} />
    </div>
  );
};

export default SingleMovie;
