import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { API_ENDPOINT } from "./context";
import { useFetch } from "./useFetch";
import Error from "./Error";
import { useGlobalContext } from "./context";

const SingleMovie = () => {
  const { loading, data, movie, setMovie } = useGlobalContext();
  const { id } = useParams();
  // const [loading, setLoading] = useState(true);
  // const [movie, setMovie] = useState({});
  useFetch(`${API_ENDPOINT}&i=${id}`);

  if (data) {
    console.log("single data", data);
    setMovie(data);
    // setMovie(data);
    // const { Search: movies } = data;
    // setMovies(movies);
  }

  useEffect(() => {}, []);

  // const fetchMovie = async () => {
  //   setLoading(true);
  //   try {
  //     const response = await fetch(`${API_ENDPOINT}&i=${id}`);
  //     const data = await response.json();
  //     const { Response } = data;
  //     console.log(Response);
  //     if (Response === "False") {
  //       console.log("no movie");
  //     } else {
  //       setMovie(data);
  //       // console.log(data);
  //     }
  //   } catch (error) {
  //     console.log(error);
  //   }
  //   setLoading(false);
  // };

  useEffect(() => {
    // fetchMovie();
  }, [loading]);

  if (loading) return <div className="loading" />;
  console.log(movie);

  const { Title: title, Year: year, Plot: description, Poster: image } = movie;
  // console.log(!title);
  if (!title) return <Error />;
  // console.log("movie", movie, movie == {});
  return (
    <section className="single-movie">
      <img src={image} alt={title} />
      <div className="single-movie-info">
        <h2>{title}</h2>
        <p>{description}</p>
        <h4>{year}</h4>
        <Link to="/" className="btn">
          back to movies
        </Link>
      </div>
    </section>
  );
};

export default SingleMovie;
